import {BookSearchModel} from "../page/HomePage";

export async function search(data:BookSearchModel) {
    const response = await fetch("http://localhost:8080/book/search", { body: JSON.stringify(data), method: "POST", headers: {
            "Content-Type": "application/json",
        }})

    if(response.status !== 200) {
        throw new Error("Not logged in");
    }

    debugger;
    return response.json();
}

export async function buy(id:number) {
    const response = await fetch(`http://localhost:8080/book/buy/${id}`, { method: "GET" , credentials: "include"})

    if(response.status !== 200) {
        throw new Error("Not logged in");
    }

    debugger;
    return response.json();
}
